import './Project.css';
import React from 'react';
import { useSpring, animated } from "react-spring";
import NewWindow from 'react-new-window'
import Modal from 'react-modal';
import ProjectModal from './ProjectModal'

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)',
    width: '800px',
    height: '800px',

  },
  overlay: {zIndex: 1000}
};

function Project(props) {
  const [{yText, yButton, opacity, opacityText, color}, set] = useSpring(() => ({
    yText: -200,
    yButton: 200,
    color: "white",
    opacity: 1,
    opacityText : 0,
    config: { friction: 25 }}))

  const [opacityTest, setOpacity] = useSpring(() => ({
    opacity:0

  }))

  const [modalIsOpen,setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function closeModal(){
    setIsOpen(false);
  }

  function sayHello() {
    alert('Hello!');
  }

  return (
    <div
      className="projectImage"
      onMouseMove={() => set({ yText: -100, yButton: 100,opacity: 0, opacityText: 1})}
      onMouseLeave={() => set({ yText: -200, yButton: 200,opacity: 1, opacityText: 0})}

    >
      <animated.img style={{opacity}} src={props.image} />
      <animated.div
        style={Object.assign({},
          {opacity: opacityText.interpolate((o) => `${o}`)},
          {transform: yText.interpolate((v) => `translateY(${v}%`)})}
        className="projectText">
        TEXT1
      </animated.div>
      <animated.button
        onMouseDown={openModal}
        style={Object.assign({},
          {opacity: opacityText.interpolate((o) => `${o}`)},
          {transform: yButton.interpolate((v) => `translateY(${v}%`)})}
        className="projectButton">
        LEARN MORE
      </animated.button>
      <ProjectModal/>

    </div>
  )
}

export default Project;
