import './SocialIconComp.css';
import { SocialIcon } from 'react-social-icons';
import React, { useState } from 'react';
import { useSpring, animated } from "react-spring";


function SocialIconComp () {

  const[color,setColor] = useState("rgba(52,58,64,1)");

  const [size, setSize] = useSpring(() => ({
    witdh: 50,
    height:50,
    config: { mass: 5, tension: 350, friction: 40 } }))

  return (
    <div
      className ="socialIconComp"
      style={{backgroundColor:color}}
      onMouseMove={()=>setColor("rgba(246, 36, 89, 1)")}
      onMouseLeave={()=>setColor("rgba(52,58,64,1)")}>
      <animated.div
      onMouseEnter={()=>setSize({width:20,height:20})}
      onMouseLeave={()=>setSize({width:50,height:50})}
      style={size}
      className="socialIconAnimated"
      >
      <SocialIcon
        url="https://www.linkedin.com/in/naehyung-kim-15a1921b4/"
        bgColor="transparent"
        fgColor ="white"/>
      </animated.div>
    </div>
  )
}

export default SocialIconComp;
